import React, { Fragment, createContext, useState } from "react";
import { Navber, Footer, CartModal } from "../partials";
import LoginSignup from "../auth/LoginSignup";
import Features from "../home/Features";

export const LayoutContext = createContext();

const Layout = ({ children }) => {
  const [showFeatures, setShowFeatures] = useState(true); // Initialize it as true to show <Features />

  const handleProductClick = () => {
    // This function will be called when a specific product is clicked.
    // You can set showFeatures to false to make <Features /> disappear.
    setShowFeatures(false);
  };

  return (
    <Fragment>
      <div className="flex-grow">
        <Navber />
        <LoginSignup />
        <CartModal />
        {/* All Children pass from here */}
        {children}
        {showFeatures && <Features />}
      </div>
      <Footer />
    </Fragment>
  );
};

export default Layout;
